mapper.select:
select RECEIPT_ID, DATA_ID, DATA_ID2, DATA_ID3, DATA_ID4, PROJECT_NAME, GL_DATE, CURRENCY_CODE, CR_AMOUNT, CR_BASE_AMOUNT, EXCHANGE_RATE, RECEIPTS_STATUS, CR_AMOUNT1, CR_BASE_AMOUNT1, METHOD_NAME, RECEIPT_NUMBER,
from HCUX_EPS_RECEIPT_DETAIL receipt
and DATA_ID = #{dataId}
and DATA_ID2 = #{dataId2}
and DATA_ID3 = #{dataId3}
and DATA_ID4 = #{dataId4}
