mapper.select:
select round(sum(v6.back_tax),2) tax_refunds--退税额
from cux_0_back_tax_v@ebs_db v6
where v6.project_number=#{projectNum}

SELECT customs_amount, SPECIFIED_AMOUNT, RECEIPT_CNY, EQUIVALENT_AMOUNT, HAS_PAYMENT, UN_PAYMENT, DOMESTIC_FREIGHT, OTHER_EXPORT_EXPENSES, TAX_REFUNDS FROM ( select round(sum(HECD.TOTAL_PRICE),2)customs_amount FROM HCUX_EPS_CUSTOMS_DETAIL HECD where HECD.MODIFY_FLAG='N' and HECD.INVOICE_NUMBER=#{projectNum} ),--报关金额 ( select round(sum(CR_AMOUNT),2)SPECIFIED_AMOUNT from HCUX_EPS_RECEIPT_DETAIL HERD where HERD.METHOD_NAME in('XJ_国外_电汇','XJ_国外_电汇'||'&'||'预付款退款', 'XJ_国内_电汇','XJ_国内_电汇'||'&'||'预付款退款','XJ_虚拟收款','XJ_虚拟收款'||'&'||'预付款退款') AND HERD.currency_code !='CNY' AND HERD.PROJECT_NAME=#{projectNum} ),--收汇金额 ( select round(sum(CR_AMOUNT),2)RECEIPT_CNY from HCUX_EPS_RECEIPT_DETAIL HERD where HERD.METHOD_NAME in('XJ_国外_电汇','XJ_国外_电汇'||'&'||'预付款退款', 'XJ_国内_电汇','XJ_国内_电汇'||'&'||'预付款退款','XJ_虚拟收款','XJ_虚拟收款'||'&'||'预付款退款') and HERD.currency_Code='CNY' AND HERD.PROJECT_NAME=#{projectNum} ),--人民币收款 ( select round(sum(CR_BASE_AMOUNT),2)EQUIVALENT_AMOUNT from HCUX_EPS_RECEIPT_DETAIL HERD where HERD.METHOD_NAME in('XJ_国外_电汇','XJ_国外_电汇'||'&'||'预付款退款', 'XJ_国内_电汇','XJ_国内_电汇'||'&'||'预付款退款','XJ_虚拟收款','XJ_虚拟收款'||'&'||'预付款退款') AND HERD.currency_code !='CNY' AND HERD.PROJECT_NAME=#{projectNum} ),--等值金额 ( select round(sum(BASE_AMOUNT),2)HAS_PAYMENT from HCUX_EPS_PAYMENT_DETAIL HEPD where HEPD.EXPENDITURE_TYPE='货款' AND HEPD.PROJECT_NUM=#{projectNum} and HEPD.STATUS='已付' ),--已付货款 ( select round(sum(REMAIN_AMOUNT),2)UN_PAYMENT from HCUX_EPS_BILL HEB where HEB.ACCOUNTS='应付账款' and HEB.PROJECT_NAME=#{projectNum} ),--未付货款 ( select round(sum(BASE_AMOUNT),2)DOMESTIC_FREIGHT from HCUX_EPS_PAYMENT_DETAIL HEPD where HEPD.EXPENDITURE_TYPE='出口-国内运费' AND HEPD.PROJECT_NUM=#{projectNum} and HEPD.STATUS='已付'
and to_char(HEPD.PAYMENT_DATE,'yyyy-MM-dd') >=('2019-06-01')
),--国内运费 ( select round(sum(functional_currency_amount),2)OTHER_EXPORT_EXPENSES from cux_0_Export_cost_v@ebs_db CECV where CECV.PROJECT_NUMBER=#{projectNum} ),--其他出口费用 ( select round(sum(REMAIN_AMOUNT),2)TAX_REFUNDS from HCUX_EPS_BILL HEB where HEB.PROJECT_NAME=#{projectNum} AND HEB.ACCOUNTS='应收外汇账款' )--退税款

select STATEMENTS_ID, PROJECT_NUM, EXPORT_ADJUSTMENT, CUSTOMS_AMOUNT, SPECIFIED_AMOUNT, AVERAGE_RATES, EQUIVALENT_AMOUNT, HAS_PAYMENT, UN_PAYMENT, PREPAID_FREIGHT, OTHER_EXPORT_EXPENSES, TAX_REFUNDS, AGENCY_FEE, BALANCE, BALANCE1, RECEIPT_CNY, DOMESTIC_FREIGHT, INCOME_EXPENSES, FINANCIAL_COST, REIMBURSEMENT, AGENCY_FEE1, REMARK,
from HCUX_EPS_STATEMENTS HES where 1=1
and HES.PROJECT_NUM=#{projectNum}
