mapper.select:
select HLOH.ORDER_ID, HLOH.CONTRACT_CODE_ONE, HLOH.CONTRACT_CODE_TWO, HLOH.CONTRACT_CODE_THREE, HLOH.ORDER_NUMBER, HLOH.CARRIER, HLOH.CONSIGNOR_NAME, HLOH.DRAW_GOODS_UNIT, HLOH.DRAW_GOODS_PROVINCE, HLOH.DRAW_GOODS_CITY, HLOH.DRAW_GOODS_AREA, HLOH.DRAW_GOODS_ADDRESS, HLOH.DRAW_GOODS_PEOPLE, HLOH.DRAW_GOODS_MOBILE, HLOH.CONSIGNEE_UNIT, HLOH.CONSIGNEE_PROVINCE, HLOH.CONSIGNEE_CITY, HLOH.CONSIGNEE_AREA, HLOH.CONSIGNEE_ADDRESS, HLOH.CONSIGNEE, HLOH.CONSIGNEE_MOBILE, HLOH.DRAW_GOODS_DATE, HLOH.ARRIVED_DATE, HLOH.REMARK, HLOH.STATUS, HLOH.LINK, HLOH.DELETE_FLAG, HLOH.REJECT_REASON, HLOH.ZH_ORDER_ID, HLOH.ZH_ORDER_NUMBER, HLOH.CONSIGNOR_SELF_NUMBER, HE.NAME USER_NAME,
from HCUX_LM_ORDER_HEADER HLOH LEFT JOIN SYS_USER SU ON HLOH.CREATED_BY = SU.USER_ID LEFT JOIN HR_EMPLOYEE HE ON SU.EMPLOYEE_ID = HE.EMPLOYEE_ID where 1=1
and ORDER_ID =#{orderId}
and CONTRACT_CODE_ONE like '%'||#{contractCodeOne}||'%' or CONTRACT_CODE_TWO like '%'||#{contractCodeOne}||'%' or CONTRACT_CODE_THREE like '%'||#{contractCodeOne}||'%'
and ORDER_NUMBER like '%'||#{orderNumber}||'%'
and CARRIER like '%'||#{carrier}||'%'
and CONSIGNEE_UNIT like '%'||#{consigneeUnit}||'%'
and DRAW_GOODS_UNIT like '%'||#{drawGoodsUnit}||'%'
and ARRIVED_DATE >=#{arrivedDateFrom}
and ARRIVED_DATE <=#{arrivedDateTo}
and DRAW_GOODS_DATE >=#{drawGoodsDateFrom}
and DRAW_GOODS_DATE <=#{drawGoodsDateTo}
and HE.NAME like '%'||#{userName}||'%'
and HLOH.STATUS = #{status}
order by DRAW_GOODS_DATE desc

select HLOH.ORDER_ID, HLOH.CONTRACT_CODE_ONE, HLOH.CONTRACT_CODE_TWO, HLOH.CONTRACT_CODE_THREE, HLOH.ORDER_NUMBER, HLOH.CARRIER, HLOH.CONSIGNOR_NAME, HLOH.DRAW_GOODS_UNIT, HLOH.DRAW_GOODS_PROVINCE, HLOH.DRAW_GOODS_CITY, HLOH.DRAW_GOODS_AREA, HLOH.DRAW_GOODS_ADDRESS, HLOH.DRAW_GOODS_PEOPLE, HLOH.DRAW_GOODS_MOBILE, HLOH.CONSIGNEE_UNIT, HLOH.CONSIGNEE_PROVINCE, HLOH.CONSIGNEE_CITY, HLOH.CONSIGNEE_AREA, HLOH.CONSIGNEE_ADDRESS, HLOH.CONSIGNEE, HLOH.CONSIGNEE_MOBILE, HLOH.DRAW_GOODS_DATE, HLOH.ARRIVED_DATE, HLOH.REMARK, HLOH.STATUS, HLOH.LINK, HLOH.DELETE_FLAG, HLOH.REJECT_REASON, HLOH.ZH_ORDER_ID, HLOH.ZH_ORDER_NUMBER, HLOH.CONSIGNOR_SELF_NUMBER,
from HCUX_LM_ORDER_HEADER HLOH
AND HLOH.ZH_ORDER_ID =#{zhOrderId}
