mapper.select:
SELECT sum(HSEP.MIN_AGENCY_FEE)TOTAL_MIN_AGENCY_FEE FROM HCUX_SYS_ENTRUST_PARTY HSEP join SYS_USER SU on lower(SU.user_name)=lower(HSEP.ENTRUST_CODE)
SU.USER_ID=#{userId}
lower(SU.USER_Name)=lower(#{userName})

SELECT HSEP.SERIAL_NUMBER, HSEP.STATEMENT_FLAG FROM HCUX_SYS_ENTRUST_PARTY HSEP join SYS_USER SU on lower(SU.user_name)=lower(HSEP.ENTRUST_CODE)
SU.USER_ID=#{userId}
lower(SU.USER_Name)=lower(#{userName})
